package coreJavaTraiing;

public class PrintTriangle2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0; i<=4; i++)
		{
			
			for(int j=1; j<=i; j++)
			{
				System.out.print(j);
				System.out.print("\t");
				
			}
			System.out.println("");
		}

	}

}
